import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { Row, Button } from "reactstrap";

const Product = (props) => (
  <tr>
    <td>{props.product.name}</td>
    <td>{props.product.category}</td>
    <td>{props.product.date.substring(0, 10)}</td>
    <td>
      <Link
        to={"/products/edit/" + props.product._id}
        className="btn btn-primary"
      >
        edit
      </Link>{" "}
      <a
        href="#"
        className="btn btn-secondary"
        onClick={() => {
          props.deleteProduct(props.product._id);
        }}
      >
        delete
      </a>
    </td>
  </tr>
);

// const styles = (theme) => ({
//   div: {
//     margin: theme.spacing(3),
//     padding: theme.spacing(2),
//   },
// });

export default class Products extends Component {
  constructor(props) {
    super(props);

    this.deleteProduct = this.deleteProduct.bind(this);

    this.state = {
      products: [],
      categories: [],
      category: "",
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/products/")
      .then((response) => {
        this.setState({ products: response.data });
        console.log(this.state.products);
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get("http://localhost:5000/categories/")
      .then((response) => {
        console.log(this.state.categories + "LOL");
        this.setState({ categories: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  deleteProduct(id) {
    axios.delete("http://localhost:5000/products/" + id).then((response) => {
      console.log(response.data);
    });

    this.setState({
      products: this.state.products.filter((el) => el._id !== id),
    });
  }

  productsList() {
    return this.state.products.map((currentProduct) => {
      return (
        <Product
          product={currentProduct}
          deleteProduct={this.deleteProduct}
          key={currentProduct._id}
        />
      );
    });
  }

  onChangeCategory(e) {
    this.setState({
      category: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <Row>
          <h3 style={{ margin: 10 }}>Products</h3>
          <Button color="success" style={{ margin: 10 }}>
            All
          </Button>
          <select
            ref="userInput"
            required
            className="form-control"
            onChange={this.onChangeCategory}
            value={this.state.categories[0]}
          >
            {this.state.categories.map(function (category) {
              return (
                <option key={category} value={category}>
                  {category}
                </option>
              );
            })}
          </select>
        </Row>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Name</th>
              <th>Category</th>
              <th>Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>{this.productsList()}</tbody>
        </table>
      </div>
    );
  }
}
