import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Category = (props) => (
  <tr>
    <td>{props.category.name}</td>
    <td>
      <Link to={"/edit/" + props.category._id} className="btn btn-primary">
        edit
      </Link>{" "}
      <a
        href="#"
        className="btn btn-secondary"
        onClick={() => {
          props.deleteCategory(props.category._id);
        }}
      >
        delete
      </a>
    </td>
  </tr>
);

export default class Categories extends Component {
  constructor(props) {
    super(props);

    this.deleteCategory = this.deleteCategory.bind(this);

    this.state = { categories: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/categories/")
      .then((response) => {
        this.setState({ categories: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  deleteCategory(id) {
    axios.delete("http://localhost:5000/category/" + id).then((response) => {
      console.log(response.data);
    });

    this.setState({
      categories: this.state.category.filter((el) => el._id !== id),
    });
  }

  categoriesList() {
    return this.state.categories.map((currentCategory) => {
      return (
        <Category
          category={currentCategory}
          deleteCategory={this.deleteCategory}
          key={currentCategory._id}
        />
      );
    });
  }

  render() {
    return (
      <div>
        <h3>Categories</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Name</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>{this.categoriesList()}</tbody>
        </table>
      </div>
    );
  }
}
